 * @var array
 */
	public $config = array(
		'persistent' => false,
		'host' => 'localhost',
		'protocol' => 'tcp',
		'port' => 80,
		'timeout' => 30,
		'ssl_verify_peer' => true,
		'ssl_allow_self_signed' => false,
		'ssl_verify_depth' => 5,
		'ssl_verify_host' => true,
