		$expected = "%d ends in 2-4, not 12-14 (translated)";
		$this->assertEquals($expected, $result);

		$result = __n('%d = 1', '%d = 0 or > 1', (float)5);
		$expected = "%d everything else (translated)";
		$this->assertEquals($expected, $result);
	}

