 */
	public function testTimeDefinition() {
		Configure::write('Config.language', 'po');
		$result = __c('d_fmt', 5);
		$expected = '%m/%d/%Y';
		$this->assertEquals($expected, $result);

		$result = __c('am_pm', 5);
		$expected = array('AM', 'PM');
		$this->assertEquals($expected, $result);

