 */
	protected function _pluralFromCore($upTo = 25) {
		$plurals = array();
		for ($number = 0; $number <= $upTo; $number++) {
			$plurals[] = sprintf(__n('%d = 1 (from core)', '%d = 0 or > 1 (from core)', (float)$number), (float)$number);
