/**
 * Test that behavior priority
 *
 * @return void
 */
	public function testBehaviorOrderCallbacks() {
		$model = ClassRegistry::init('Orangutan');
		$model->Behaviors->init('Orangutan', array(
			'Second' => array('priority' => 9),
			'Third',
			'First' => array('priority' => 8),
		));

		$this->assertEmpty($model->called);

		$model->find('first');
		$expected = array(
			'FirstBehavior',
			'SecondBehavior',
			'ThirdBehavior',
		);
		$this->assertEquals($expected, $model->called);

		$model->called = array();
		$model->Behaviors->load('Third', array('priority' => 1));

		$model->find('first');
		$expected = array(
			'ThirdBehavior',
			'FirstBehavior',
			'SecondBehavior'
		);
		$this->assertEquals($expected, $model->called);

		$model->called = array();
		$model->Behaviors->load('First');

		$model->find('first');
		$expected = array(
			'ThirdBehavior',
			'SecondBehavior',
			'FirstBehavior'
		);
		$this->assertEquals($expected, $model->called);

		$model->called = array();
		$model->Behaviors->unload('Third');

		$model->find('first');
		$expected = array(
			'SecondBehavior',
			'FirstBehavior'
		);
		$this->assertEquals($expected, $model->called);

		$model->called = array();
		$model->Behaviors->disable('Second');

		$model->find('first');
		$expected = array(
			'FirstBehavior'
		);
		$this->assertEquals($expected, $model->called);

		$model->called = array();
		$model->Behaviors->enable('Second');

		$model->find('first');
		$expected = array(
			'SecondBehavior',
			'FirstBehavior'
		);
		$this->assertEquals($expected, $model->called);
	}

