			'users' => array('title' => 'Users', 'weight' => 1),
			'comments' => array('title' => 'Comments', 'weight' => 2),
			'blogs' => array('title' => 'Blogs', 'weight' => 3),
			);
		$result = Set::sort($menus, '{[a-z]+}.weight', 'ASC');
		$this->assertEquals($expected, $result);
	}

/**
