		$xml = '<tag type="myType">0</tag>';
		$obj = Xml::build($xml);
		$expected = array(
			'tag' => array(
				'@type' => 'myType',
				'@' => 0
			)
		);
		$this->assertEquals($expected, Xml::toArray($obj));
	}

